import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { TranslateModule, TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-career',
  standalone: true,
  imports: [TranslateModule, CommonModule],
  templateUrl: './career.component.html',
  styleUrl: './career.component.scss'
})
export class CareerComponent implements OnInit {
  constructor(){

  }

  ngOnInit(): void {
  }

}
<div class="career">
  <div class="title">
    {{ "career.title" | translate }}
  </div>

  <div class="content">
    <div class="w-50">
      <div class="sub-title">
        {{ "career.slogan" | translate }}
      </div>
      <div class="thought">
        {{ "career.thought" | translate }}
      </div>

      <div *ngFor="let testi of 'career.testimony' | translate; let i = index" [class]="'testimony-' + i">
        "{{testi}}"
      </div>
    </div>

    <div class="w-50">
      <img src="https://placehold.co/700x540" />
      <img src="https://placehold.co/700x540" />
    </div>
  </div>

  <div class="mission">
      <div class="title color-primary">
          {{'mission.title' | translate}}
      </div>
      <div class="description color-primary">
        {{'mission.description' | translate}}
      </div>
      <div class="sub-title color-primary">
        {{'mission.perksTitle' | translate}}
      </div>

      <div *ngFor="let perk of 'mission.perks' | translate; let i = index">
         {{i + 1}}. {{perk}}
      </div>
  </div>
</div>
