import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { TranslateModule } from '@ngx-translate/core';
import { SwiperModule } from 'swiper/angular';
import SwiperCore, { SwiperOptions, Navigation, Pagination, Scrollbar, A11y } from 'swiper';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);


@Component({
  selector: 'app-about-us',
  standalone: true,
  imports: [CommonModule, TranslateModule, SwiperModule],
  templateUrl: './about-us.component.html',
  styleUrl: './about-us.component.scss'
})
export class AboutUsComponent {
  config: SwiperOptions = {
    slidesPerView: 1,
    navigation: true,
    pagination: { clickable: true },
    scrollbar: { draggable: true },

  };

  onSwiper(swiper) {
    console.log(swiper);
  }
  onSlideChange() {
    console.log('slide change');
  }
}
<div class="aboutUs">
  <div class="left-section">
    <div class="title">
      {{ "about.title" | translate }}
    </div>

    <div class="subTitle">
      {{ "about.subTitle" | translate }}
    </div>

    <div class="description">
      {{ "about.description" | translate }}
    </div>
  </div>

  <div class="right-section">
    <div class="description">
      {{ "about.value" | translate }}
    </div>

    <swiper
    [config]="config"
    (swiper)="onSwiper($event)"
    (slideChange)="onSlideChange()"
    >
    <ng-template swiperSlide><img src="https://placehold.co/600x210"></ng-template>
    <ng-template swiperSlide><img src="https://placehold.co/600x210"></ng-template>
    <ng-template swiperSlide><img src="https://placehold.co/600x210"></ng-template>
    <ng-template swiperSlide><img src="https://placehold.co/600x210"></ng-template>
    <ng-template swiperSlide><img src="https://placehold.co/600x210"></ng-template>
    <ng-template swiperSlide><img src="https://placehold.co/600x210"></ng-template>
    </swiper>
  </div>
</div>
