import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { TranslateModule, TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-career',
  standalone: true,
  imports: [TranslateModule, CommonModule],
  templateUrl: './career.component.html',
  styleUrl: './career.component.scss'
})
export class CareerComponent implements OnInit {
  public testimonyKeys: string [] =[];

  constructor(private translateService: TranslateService){

  }

  ngOnInit(): void {
    this.translateService.getTranslation('en').subscribe(data => {
      this.testimonyKeys = data.career.testimony
    })
  }

}
<div class="career">
  <div class="title">
    {{ "career.title" | translate }}
  </div>

  <div class="content">
    <div class="w-50">
      <div class="sub-title">
        {{ "career.slogan" | translate }}
      </div>
      <div class="thought">
        {{ "career.thought" | translate }}
      </div>

      <div *ngFor="let testi of 'career.testimony' | translate; let i = index" [class]="'testimony-' + i">
        {{testi}}
      </div>
    </div>

    <div class="w-50">
      <img src="https://placehold.co/700x540" />
      <img src="https://placehold.co/700x540" />
    </div>
  </div>
</div>
