import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';

@Component({
  selector: 'app-contact-us',
  standalone: true,
  imports: [CommonModule, TranslateModule, ReactiveFormsModule],
  templateUrl: './contact-us.component.html',
  styleUrl: './contact-us.component.scss'
})
export class ContactUsComponent {
  public formGroup: FormGroup;

  constructor() {
    this.formGroup = new FormGroup({
      name: new FormControl('', Validators.required),
      email: new FormControl('', Validators.required),
      phone: new FormControl('', Validators.required),
      message: new FormControl('', Validators.required),
    })
  }
}
<div class="title">
    {{'contactUs.title' | translate}}
</div>

<div class="contactUs">
    <div class="left-content w-50">
        <div class="box-container">
            <div class="sub-title">
                {{'contactUs.subTitle' | translate}}
            </div>
            <div class="description">
                {{'contactUs.description' | translate}}
            </div>

            <div class="info">
                <img class="icon" src="assets/icons/contact-white.svg" />
                {{'contactUs.phoneNumber' | translate}}
            </div>
            <div class="info">
                <img class="icon" src="assets/icons/mail-white.svg" />
                {{'contactUs.email' | translate}}
            </div>
            <div class="info">
                <img class="icon" src="assets/icons/location-white.svg" />
                {{'contactUs.address' | translate}}
            </div>
        </div>
    </div>

    <div class="righ-content w-50">
        <form [formGroup]="formGroup">
            <mat-form-field class="example-full-width">
                <mat-label>Nama</mat-label>
                <input type="name" matInput [formControl]="emailFormControl" [errorStateMatcher]="matcher"
                       placeholder="Ex. pat@example.com">
              </mat-form-field>
        </form>
    </div>
</div>
