import { Component } from '@angular/core';
import { TranslateModule } from '@ngx-translate/core';

@Component({
  selector: 'app-career',
  standalone: true,
  imports: [TranslateModule],
  templateUrl: './career.component.html',
  styleUrl: './career.component.scss'
})
export class CareerComponent {

}
<div class="career">
  <div class="title">
    {{ "career.title" | translate }}
  </div>

  <div class="content">
    <div class="w-50">
      <div class="sub-title">
        {{ "career.slogan" | translate }}
      </div>
      <div class="thought">
        {{ "career.thought" | translate }}
      </div>
    </div>

    <div class="w-50">
      <img src="https://placehold.co/700x540" />
      <img src="https://placehold.co/700x540" />
    </div>
  </div>
</div>
